// LoginPage.js
import React, { useState } from 'react';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';
import Header from './Header'; // Assuming the path is correct
import Footer from './Footer'; // Assuming the path is correct

const LoginPage = ({ setIsLoggedIn }) => {
  const [showLogin, setShowLogin] = useState(true);

  const toggleForm = () => setShowLogin(!showLogin);

  return (
    <div>
      <Header />
      {showLogin ? (
        <LoginForm
          onLoginSubmit={setIsLoggedIn} // Adjust as necessary based on actual login logic
          onSwitchToSignup={toggleForm}
        />
      ) : (
        <SignupForm
          onSubmit={setIsLoggedIn} // Adjust as necessary based on actual signup logic
          switchToLogin={toggleForm}
        />
      )}
      <Footer />
    </div>
  );
};

export default LoginPage;
