// LoginPage.js - Main container for login and signup forms
import React, { useState } from 'react';
import Header from './Header'; // Assuming Header component is defined elsewhere
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';
import Footer from './Footer'; // Assuming Footer component is defined elsewhere

const LoginPage = () => {
    const [isLoginVisible, setIsLoginVisible] = useState(true);

    const toggleForm = () => setIsLoginVisible(!isLoginVisible);

    const loginHandler = (data) => {
        // Logic to handle login (e.g., API call)
        console.log('Logging in with', data);
    };

    const signupHandler = (data) => {
        // Logic to handle signup (e.g., API call)
        console.log('Signing up with', data);
    };

    return (
        <div>
            <Header />
            {isLoginVisible ? (
                <LoginForm onSwitchToSignup={toggleForm} onLoginSubmit={loginHandler} />
            ) : (
                <SignupForm onSwitchToLogin={toggleForm} onSignupSubmit={signupHandler} />
            )}
            <Footer />
        </div>
    );
};

export default LoginPage;
